// Macro to create a random NPC, being sent in a whisper to GMs on the server.

const pack = game.packs.get("shadowdark.rollable-tables");

const namesTableID = pack.index.find(o => o.name === "The Lost Citadel: Beastmen Names")._id;
const namesTable = await pack.getDocument(namesTableID);
const namesResult = await namesTable.draw({displayChat: false});
const names = namesResult.results.pop().getChatText();

const appearanceTableID = pack.index.find(o => o.name === "The Lost Citadel: Beastmen Appearances")._id;
const appearanceTable = await pack.getDocument(appearanceTableID);
const appearanceResult = await appearanceTable.draw({displayChat: false});
const appearance = appearanceResult.results.pop().getChatText();

const behaviorTableID = pack.index.find(o => o.name === "The Lost Citadel: Beastmen Behaviors")._id;
const behaviorTable = await pack.getDocument(behaviorTableID);
const behaviorResult = await behaviorTable.draw({displayChat: false});
const behavior = behaviorResult.results.pop().getChatText();

const message = `<p>A <b>${appearance}</b> Beastman named <b>${names}</b> 
is <b>${behavior}</b>.</p>`;

const chatData = {
  user: game.user._id,
  speaker: ChatMessage.getSpeaker(),
  content: message,
  whisper: game.users.filter(u => u.isGM).map(u => u._id),
};

ChatMessage.create(chatData, {});
